import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'web';
}
<style>
  :host {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    font-size: 14px;
    color: #333;
    box-sizing: border-box;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }


  .toolbar {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 60px;
    display: flex;
    align-items: center;
    background-color: #1976d2;
    color: white;
    font-weight: 600;
  }

  .toolbar img {
    margin: 0 16px;
  }

  .toolbar .material-symbols-outlined {
    vertical-align: middle;
  }

  .toolbar #contact {
    color: #ffffff;
    height: 40px;
    margin: 0 8px;
    line-height: 40px;
    text-decoration: none;
  }

  .content {
    display: flex;
    margin: 82px auto 32px;
    padding: 0 16px;
    max-width: 960px;
    flex-direction: column;
    align-items: center;
  }
</style>

<!-- Toolbar -->
<div class="toolbar" role="banner">
  <img height="40" alt="Logo" src="/assets/img/logo.svg" />
  <span>Scheduler &bull; Silicon Valley Buddhist Center</span>
    <div class="spacer"></div>
    <a id="contact" href="mailto:dean@codeka.com">
      <span class="material-symbols-outlined">mail</span> Contact
    </a>
</div>

<div class="content" role="main">
  <router-outlet></router-outlet>
</div>
